* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

#html {
    /* 文本自动折行 */
    word-break: break-all;
}

.div1Wrapper {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translateY(-50%);
}

#div1::after {
    content: '';
    display: block;
}

#div1::before {
    content: '';
    display: block;
}

@media (max-width: 500px) {
    #html {
        height: 50vh;
        overflow: hidden;
    }

    .div1Wrapper {
        height: 50vh;
        position: relative;
        top: 0;
        left: 0;
        transform: none;
    }

    #div1 {
        position: relative;
        top: 0;
        left: 0;
        margin-left: auto;
        margin-right: auto;
    }
}
